mapper.select:
select count(*)
	from tbl_member
where member_id = #{_parameter}

mapper.update:
update tbl_member
	set member_pw = #{memberPw},
		member_name = #{memberName},
		member_email = #{memberEmail},
		member_phone = #{memberPhone}
	where member_id = #{memberId}

mapper.select:
select count(*)
	from tbl_member
where member_email = #{_parameter}
